          "query": {
            "bool": {
              "must": [
                {
                  "query_string": {
                    "query": "EventID:\"106\"",
                    "analyze_wildcard": true
                  }
                }
              ],
              "filter": {
                "range": {
                  "timestamp": {
                    "gte": "now-30m/m"
